using System;using System.Threading.Tasks;using OrderAppTests.Models;using RestSharp;namespace OrderAppTests;public class OrderController : BaseController{    public record CreateOrderResponse(Guid Id, decimal TotalPrice);    public async Task<RestResponse<CreateOrderResponse>> Create(Order order, DateTime date)    {        var request = new RestRequest("/Order")            .AddQueryParameter("date", date)            .AddBody(order);        var response = await Client.ExecutePostAsync<CreateOrderResponse>(request);        return response;    }    public async Task<RestResponse<Order>> GetOrder(Guid id)    {        var request = new RestRequest("/Order").AddQueryParameter("id", id);        var response = await Client.ExecuteAsync<Order>(request);        return response;    }        public async Task<RestResponse<Order>> Update(Guid id, Order order, DateTime date, bool cancel = false)    {        var request = new RestRequest("/Order")            .AddQueryParameter("id", id)            .AddQueryParameter("date", date)            .AddQueryParameter("cancel", cancel)            .AddBody(order);        var response = await Client.ExecutePutAsync<Order>(request);        return response;    }}